import Training from "./Training"
import React, { useState, useEffect } from "react"
import Axios from 'axios'
import "./TrainingList.css"
import { store } from "../../features/store"
import { useSelector } from "react-redux"

function TrainingList(props){
    const currentUser = useSelector(state => store.getState().user)
    const [trainings, setTrainings] = useState([])
    const [isClicked, setIsClicked] = useState(false)
    const [denumire, setDenumire] = useState("")
    const [data, setData] = useState("")
    const [speaker, setSpeaker] = useState("")

    useEffect(() => {
        getTrainings()
    }, [trainings]);    

    const getTrainings = () => {
        Axios.get(`http://localhost:3001/api/training/${props.entitate}`).then(response => {
            setTrainings(response.data)
        }).catch(err => console.log(err))
    }

    const handleAdd = () => {
        setIsClicked(true)
    }

    const handleSubmit = () => {
        Axios.post(`http://localhost:3001/api/training/${props.entitate}`, {
            denumire: denumire,
            data: data,
            speaker: speaker
        }).then(response => {
            console.log(response.data)
        }).catch(err => console.log(err))
        setIsClicked(false)
    }

    return(
        <>
            <h1>Traininguri</h1>
            {currentUser.functie_id === 'C' && <button onClick={handleAdd}>Adauga training</button>}
            {isClicked && 
            <div className="training-form">
                    <input
                        type="text"
                        value={denumire}
                        placeholder="Tema"
                        onChange={(e) => setDenumire(e.currentTarget.value)}
                    />
                <div>
                    <input
                        type="date"
                        value={data}
                        placeholder="Data"
                        onChange={(e) => setData(e.currentTarget.value)}
                    />
                </div>
                <div>
                        <input
                            type="text"
                            value={speaker}
                            placeholder="Speaker"
                            onChange={(e) => setSpeaker(e.currentTarget.value)}
                        />
                </div>
                <button onClick={handleSubmit}>Ok</button>
            </div>}
            <div className="trainings">
                {trainings.map((item) => {
                return <Training key={item.id} {...item} />
                })}
            </div>
        </>
    )
}

export default TrainingList
