import React from "react"
import { Link } from 'react-router-dom'
import { store } from "../../features/store"
import { useDispatch, useSelector } from "react-redux"
import { useEffect, useState } from 'react'
import Navbar from "./Navbar"
import "./Profil.css"
import { startFeedbackIntermediar } from "../../features/sessionSlice"
import Axios from 'axios'
import IndicatorItem from "../Indicator/IndicatorItem"

function Indicators(){
    const currentUser = useSelector(state => store.getState().user)
    console.log(currentUser)
    const dispatch = useDispatch();
    const [entitate, setEntitate] = useState("")
    const [isNPS, setIsNPS] = useState(false)
    const [NPS, setNPS] = useState(0)
    const entitati = ["Econosofia", "International Affairs", "Leadership Development", "Business Club", "Human Resources", "Business Development", "Public Relations", "Marketing", "Creative"]

    const handleClickLaunch = () => {
        Axios.post("http://localhost:3001/api/indicator/Human Resources", {
            indicator: "eNPS"
        }).then(response => {
            console.log(response.data)
            // getFeedback()
        }).catch(err => console.log(err))    
    }

    const getNPS = () => {
        entitati.forEach(entitate => {
            Axios.get(`http://localhost:3001/api/indicator/management/${entitate}/eNPS`).then(response => {
                const feedback = response.data
                console.log(response.data)
                let scoruri = feedback.map(f => f.scor)
                let count = 0
                let promoters = 0
                let detractors = 0
                scoruri.forEach(element => {
                    if(element >= 9) {
                        promoters++
                    } else if(element <=6) {
                        detractors++
                    }
                    count++
                });
                let procent = (promoters/count * 100) - (detractors/count * 100)
                setIsNPS(true)
                setNPS(procent.toFixed(1))
                // console.log(tema,speakerFb,raport, suma, count, medie)
            })
            .catch(err => console.log(err))    
        });
    }

    return(
        <>
            <div>
                <h1>eNPS</h1>
                <button onClick={handleClickLaunch}>Lanseaza HR</button>
                {entitati.map((item) => {
                    return <IndicatorItem key={item} {...item} />
                })}
            </div>
        </>
    )
}

export default Indicators
