import Axios from "axios"
import { useEffect, useState } from 'react'

function TrainingFeedbackManagement({id,entitate, denumire, data, speaker}){
    const [tema, setTema] = useState([])

    const getFeedback = () => {
        Axios.get(`http://localhost:3001/api/training/${id}`).then(response => {
            const feedback = response.data
            let tema = feedback.map(f => f.tema)
            setTema(tema)
        })
        .catch(err => console.log(err))
    }

    useEffect(() => {
        getFeedback()
      }, [])

    return(
        <article className='training'>
            <h1>{denumire}</h1>
            <h3>{data}</h3>
            <h2>{speaker}</h2>
        </article>
    )
}

export default TrainingFeedbackManagement
