import { Link, Navigate, useLinkClickHandler } from 'react-router-dom'
import { useNavigate } from 'react-router-dom';
import "./Training.css"

function Training({id,entitate, denumire, data, speaker}){
    const navigate = useNavigate()

    const handleClickFeedback = () => {
        navigate(`/dashboard/training/${entitate}/${id}`, {
            denumire: denumire,
            speaker: speaker
        })
    }

    return(
        <article className='training'>
            <h1>{denumire}</h1>
            <h3>{data}</h3>
            <h2>{speaker}</h2>
            <Link to={`/dashboard/training/${entitate}/${id}`} className="feedback" style={{ textDecoration: 'none' }} state={{denumire: denumire}}>Feedback</Link>
            {/* <button onClick={handleClickFeedback}>Feedback</button> */}
        </article>
    )
}

export default Training
